/**
 * Logs the given argument in the browser's console highlighting it with a yellow background and red color.
 * @method   warning
 * @global
 * @example
 * ```javascript
 * bbn.fn.warning('whatever you want to log as a warning');
 * ```
 * @memberof bbn.fn
 * @param    {...any} args
 * @returns
 */
declare const warning: (message: any) => void;
export { warning };
