/**
 * Removes duplicate values from an array.
 *
 * Takes an input array and returns a new array without duplicate values.
 *
 * @method   unique
 * @global
 * @example
 * ```javascript
 * bbn.fn.unique(["a", "b", "a", "b", "a", "b", "c", "c", "d"]);
 * // ["a", "b", "c", "d"]
 * ```
 * @memberof bbn.fn
 * @param    {Array} arr
 * @returns  {Array}
 */
declare const unique: (arr: any[]) => any[];
export { unique };
