/**
 * Returns true if the given argument is a boolean
 * @method   isBoolean
 * @global
 * @example
 * ```javascript
 * const sb = true;
 * bbn.fn.isBoolean(sb); // true
 * const sb = 1;
 * bbn.fn.isBoolean(sb); // false
 * ```
 * @memberof bbn.fn
 * @returns  {Boolean}
 */
export default function isBoolean(...args: any[]): boolean;
