/**
 * Returns true if the given argument is an integer
 * @method   isInt
 * @global
 * @example
 * ```javascript
 * bbn.fn.isInt(5);
 * // true
 * bbn.fn.isInt(0.5);
 * // false
 * bbn.fn.isInt("hello");
 * // false
 * ```
 * @memberof bbn.fn
 * @returns  {Boolean}
 */
export default function isInt(...args: any[]): boolean;
